mapper.update:
update category
set is_deleted = 1 where id in

    #{id}

mapper.select:
select

from category
where parent_id in

    #{parentId,jdbcType=BIGINT}

and category_level = #{categoryLevel,jdbcType=TINYINT}
and is_deleted = 0
order by category_rank desc

    limit #{number}

select

from category


        and category_level = #{categoryLevel}


        and parent_id = #{parentId}

    and is_deleted = 0

order by category_rank desc

    limit #{start},#{limit}
